package fundamentos;

public class AeraCircunferencia {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double raio = 3.456 ;				
		
		final double PI = 3.141592653 ;
				 
		double area = PI*(raio*raio) ;  
				
		
		System.out.println("E o ráio é " + area );
		
		raio = 10 ;
		area = PI*(raio*raio) ;
		System.out.println("E a área do circulo é  :-=>  " + area );
	}

}
